package com.pds.pay.ali;import android.text.TextUtils;import java.util.Map;/** * @author pengdaosong */public class PayResult {    private String resultStatus;    private String result;    private String memo;    private String version;    public static final String RESULT_STATUS = "resultStatus";    public static final String RESULT = "result";    public static final String MEMO = "memo";    public static final String VERSION = "version";    public PayResult(Map<String, String> rawResult) {        if (rawResult == null) {            return;        }        for (String key : rawResult.keySet()) {            if (TextUtils.equals(key, RESULT_STATUS)) {                resultStatus = rawResult.get(key);            } else if (TextUtils.equals(key, RESULT)) {                result = rawResult.get(key);            } else if (TextUtils.equals(key, MEMO)) {                memo = rawResult.get(key);            } else if (TextUtils.equals(key, VERSION)) {                version = rawResult.get(key);            }        }    }    @Override    public String toString() {        return "resultStatus={" + resultStatus + "};memo={" + memo                + "};result={" + result + "};version={" + version + "}";    }    /**     * @return the resultStatus     */    public String getResultStatus() {        return resultStatus;    }    /**     * @return the memo     */    public String getMemo() {        return memo;    }    /**     * @return the result     */    public String getResult() {        return result;    }    public String getVersion() {        return version;    }}